CREATE TRIGGER DWH.BALTUR_I1 
	AFTER INSERT ON DWH.BALTUR 
	REFERENCING NEW AS NROW 
	FOR EACH ROW 
	MODE DB2ROW 
	SET OPTION  ALWBLK = *ALLREAD , 
	ALWCPYDTA = *OPTIMIZE , 
	COMMIT = *NONE , 
	DECRESULT = (31, 31, 00) , 
	DFTRDBCOL = *NONE , 
	DYNDFTCOL = *NO , 
	DYNUSRPRF = *USER , 
	SRTSEQ = *HEX   
	BEGIN ATOMIC 
IF EXISTS ( SELECT 1 FROM DWH . BSBT_FL WHERE DWH . BSBT_FL . DAT = NROW . DAT ) 
THEN 
UPDATE DWH . BSBT_FL 
SET LAST_TS = CURRENT TIMESTAMP , ACC_CNT = DWH . BSBT_FL . ACC_CNT + 1 
WHERE DWH . BSBT_FL . DAT = NROW . DAT ; 
ELSE 
INSERT INTO DWH . BSBT_FL ( DAT ) VALUES ( NROW . DAT ) ; 
END IF ; 
END  ; 
  
