	WHEN ( QSYS2 . SUBSTRING ( NROW . ID , 1 , 1 ) <> '@' )  
	BEGIN ATOMIC DECLARE CID CHARACTER ( 20 ) ; DECLARE OOBSACC CHARACTER ( 5 ) ; DECLARE OOPSAV CHARACTER ( 1 ) ; DECLARE OOGRP CHARACTER ( 1 ) ; SET ( "SQLP_L2" . CID ) = ( SELECT DWH . BSAACC . ID FROM DWH . BSAACC WHERE DWH . BSAACC . ID = NROW . ID ) ; IF "SQLP_L2" . CID IS NULL THEN SET "SQLP_L2" . OOBSACC = QSYS2 . SUBSTRING ( NROW . ID , 1 , 5 ) ; SET "SQLP_L2" . OOPSAV = ( SELECT DWH . IMBCBAPP . AAPSAV FROM DWH . IMBCBAPP WHERE DWH . IMBCBAPP . AACBAC = "SQLP_L2" . OOBSACC ) ; IF QSYS2 . SUBSTRING ( NROW . ID , 1 , 1 ) = '3' THEN SET "SQLP_L2" . OOGRP = '3' ; ELSE SET "SQLP_L2" . OOGRP = '4' ; END IF ; INSERT INTO DWH . BSAACC ( ID , BSSAC , CCY , BSAKEY , BRCA , BSACODE , BSATYPE , BSAGRP ) VALUES ( NROW . ID , QSYS2 . SUBSTRING ( NROW . ID , 1 , 5 ) , QSYS2 . SUBSTRING ( NROW . ID , 6 , 3 ) , QSYS2 . SUBSTRING ( NROW . ID , 10 , 1 ) , QSYS2 . SUBSTRING ( QSYS2 . VARCHAR ( 10000 + QSYS2 . DECIMAL ( QSYS2 . SUBSTRING ( NROW . ID , 11 , 3 ) , 3 , 0 ) ) , 2 , 4 ) , QSYS2 . SUBSTRING ( NROW . ID , 14 , 7 ) , QSYS2 . VALUE ( "SQLP_L2" . OOPSAV , '' ) , "SQLP_L2" . OOGRP ) ; END IF ; END  ; 
  
