CREATE TABLE DWH.CLT_CBNM ( 
	COL_DESCR VARCHAR(128) CCSID 1025 DEFAULT NULL , 
	COEF_VALUE DECIMAL(11, 7) DEFAULT NULL , 
	COL_TYPE INTEGER DEFAULT NULL , 
	CB_CODE VARCHAR(10) CCSID 1025 DEFAULT NULL , 
	CLT_NAME VARCHAR(128) CCSID 1025 DEFAULT NULL , 
	DESCR_SHT VARCHAR(128) CCSID 1025 DEFAULT '' ) ; 
  
CREATE TRIGGER DWH.CLT_CBNM_AI0 
	AFTER INSERT ON DWH.CLT_CBNM 
	REFERENCING NEW AS NROW 
	FOR EACH ROW 
	MODE DB2SQL 
	SET OPTION  ALWBLK = *ALLREAD , 
	ALWCPYDTA = *OPTIMIZE , 
	COMMIT = *NONE , 
	DECRESULT = (31, 31, 00) , 
	DFTRDBCOL = *NONE , 
	DYNDFTCOL = *NO , 
	DYNUSRPRF = *USER , 
	SRTSEQ = *HEX   
	WHEN ( NROW . COL_TYPE = 0 OR TRIM ( QSYS2 . VALUE ( NROW . DESCR_SHT , '' ) ) = '' ) 
	BEGIN ATOMIC 
UPDATE DWH . CLT_CBNM T 
SET COL_TYPE = CASE WHEN T . COEF_VALUE = 1 THEN 1 
ELSE CASE WHEN T . COEF_VALUE = 0.5 THEN 2 ELSE 0 END END 
WHERE T . COL_DESCR = NROW . COL_DESCR 
AND T . COEF_VALUE = NROW . COEF_VALUE 
AND NROW . COL_TYPE = 0 
; 
UPDATE DWH . CLT_CBNM C 
SET DESCR_SHT = ( SELECT QSYS2 . MAX ( B . DESCR ) FROM DWH . COL_OBJTP A 
INNER JOIN DWH . COL_TP B 
ON A . COLLATAGREEMENTTYPE_CODE = B . COLLATAGREEMENTTYPE_CODE 
AND CURRENT DATE BETWEEN B . DAT AND B . DATTO 
WHERE TRIM ( A . DESCR ) = TRIM ( C . COL_DESCR ) 
AND CURRENT DATE BETWEEN A . DAT AND A . DATTO 
AND TRIM ( QSYS2 . VALUE ( B . DESCR , '' ) ) <> '' ) 
WHERE C . COL_DESCR = NROW . COL_DESCR 
AND C . COEF_VALUE = NROW . COEF_VALUE 
AND TRIM ( QSYS2 . VALUE ( NROW . DESCR_SHT , '' ) ) = '' 
AND EXISTS ( SELECT 1 FROM DWH . COL_OBJTP A 
INNER JOIN DWH . COL_TP B 
ON A . COLLATAGREEMENTTYPE_CODE = B . COLLATAGREEMENTTYPE_CODE 
AND CURRENT DATE BETWEEN B . DAT AND B . DATTO 
WHERE TRIM ( A . DESCR ) = TRIM ( C . COL_DESCR ) 
AND CURRENT DATE BETWEEN A . DAT AND A . DATTO 
AND TRIM ( QSYS2 . VALUE ( B . DESCR , '' ) ) <> '' 
) ; 
END  ; 
  
COMMENT ON TRIGGER DWH.CLT_CBNM_AI0 IS 'After Insert into CLT_CBNM with COL_TYPE=0' ; 
  
