CREATE TRIGGER DWH.COUNTRY_CODES_BI 
	BEFORE INSERT ON DWH.COUNTRY_CODES 
	REFERENCING NEW AS NROW 
	FOR EACH ROW 
	MODE DB2ROW 
	SET OPTION  ALWBLK = *ALLREAD , 
	ALWCPYDTA = *OPTIMIZE , 
	COMMIT = *NONE , 
	DECRESULT = (31, 31, 00) , 
	DFTRDBCOL = *NONE , 
	DYNDFTCOL = *NO , 
	DYNUSRPRF = *USER , 
	SRTSEQ = *HEX   
	BEGIN ATOMIC DECLARE CNT CHAR ( 100 ) ; 
DECLARE MSG CHAR ( 1000 ) ; 
SELECT ( CASE WHEN NROW . DAT > T . DAT 
THEN CHAR ( NROW . DAT , EUR ) ELSE CHAR ( T . DAT , EUR ) END ) 
CONCAT ' AND ' CONCAT ( CASE WHEN NROW . DATTO > T . DATTO 
THEN CHAR ( T . DATTO , EUR ) ELSE CHAR ( NROW . DATTO , EUR ) END ) 
INTO SQLP_L2 . CNT FROM DWH . COUNTRY_CODES T 
WHERE QSYS2 . INT ( NROW . CODE ) = QSYS2 . INT ( T . CODE ) 
AND T . DAT <= T . DATTO 
AND ( ( NROW . DAT BETWEEN T . DAT AND T . DATTO ) 
OR ( NROW . DATTO BETWEEN T . DAT AND T . DATTO ) 
OR ( T . DAT BETWEEN NROW . DAT AND NROW . DATTO ) ) 
FETCH FIRST 1 ROWS ONLY ; 
IF ( SQLP_L2 . CNT IS NOT NULL ) 
THEN SET SQLP_L2 . MSG = '   -    CODE=' 
CONCAT TRIM ( QSYS2 . CHAR ( NROW . CODE ) ) 
CONCAT '    ' CONCAT SQLP_L2 . CNT ; 
SIGNAL SQLSTATE VALUE '99999' SET MESSAGE_TEXT = SQLP_L2 . MSG ; 
END IF ; 
END  ; 
  
COMMENT ON TRIGGER DWH.COUNTRY_CODES_BI IS 'Check 1 row CODE in 1 moment of time' ; 
  
CREATE TRIGGER DWH.COUNTRY_CODES_BU 
	BEFORE UPDATE OF CODE , DATTO , DAT ON DWH.COUNTRY_CODES 
	REFERENCING OLD AS OROW 
	NEW AS NROW 
	FOR EACH ROW 
	MODE DB2ROW 
	SET OPTION  ALWBLK = *ALLREAD , 
	ALWCPYDTA = *OPTIMIZE , 
	COMMIT = *NONE , 
	DECRESULT = (31, 31, 00) , 
	DFTRDBCOL = *NONE , 
	DYNDFTCOL = *NO , 
	DYNUSRPRF = *USER , 
	SRTSEQ = *HEX   
	WHEN ( ( QSYS2 . INT ( NROW . CODE ) <> QSYS2 . INT ( OROW . CODE ) 
	OR NROW . DAT <> OROW . DAT 
	OR NROW . DATTO <> OROW . DATTO ) 
	AND NOT ( NROW . DATTO < NROW . DAT ) ) 
	BEGIN ATOMIC DECLARE CNT CHAR ( 100 ) ; 
DECLARE MSG CHAR ( 1000 ) ; 
SELECT ( CASE WHEN NROW . DAT > T . DAT 
THEN CHAR ( NROW . DAT , EUR ) ELSE CHAR ( T . DAT , EUR ) END ) 
CONCAT ' AND ' CONCAT ( CASE WHEN NROW . DATTO > T . DATTO 
THEN CHAR ( T . DATTO , EUR ) ELSE CHAR ( NROW . DATTO , EUR ) END ) 
INTO SQLP_L3 . CNT FROM DWH . COUNTRY_CODES T 
WHERE QSYS2 . INT ( NROW . CODE ) = QSYS2 . INT ( T . CODE ) 
AND T . DAT <= T . DATTO 
AND ( ( NROW . DAT BETWEEN T . DAT AND T . DATTO ) 
OR ( NROW . DATTO BETWEEN T . DAT AND T . DATTO ) 
OR ( T . DAT BETWEEN NROW . DAT AND NROW . DATTO ) ) 
AND NOT ( QSYS2 . INT ( OROW . CODE ) = QSYS2 . INT ( T . CODE ) 
AND OROW . DAT = T . DAT 
AND OROW . DATTO = T . DATTO ) 
FETCH FIRST 1 ROWS ONLY ; 
IF ( SQLP_L3 . CNT IS NOT NULL ) 
THEN SET SQLP_L3 . MSG = '   -    CODE=' 
CONCAT TRIM ( QSYS2 . CHAR ( NROW . CODE ) ) 
CONCAT '    ' CONCAT SQLP_L3 . CNT ; 
SIGNAL SQLSTATE VALUE '99999' SET MESSAGE_TEXT = SQLP_L3 . MSG ; END IF ; END  ; 
  
