CREATE TRIGGER DWH.F115RESTRUCT_I 
	BEFORE INSERT ON DWH.F115RESTRUCT 
	REFERENCING NEW AS NROW 
	FOR EACH ROW 
	MODE DB2ROW 
	SET OPTION  ALWBLK = *ALLREAD , 
	ALWCPYDTA = *OPTIMIZE , 
	COMMIT = *NONE , 
	CLOSQLCSR = *ENDMOD , 
	DECRESULT = (31, 31, 00) , 
	DFTRDBCOL = *NONE , 
	DYNDFTCOL = *NO , 
	DYNUSRPRF = *USER , 
	SRTSEQ = *HEX   
	BEGIN ATOMIC DECLARE CNT CHAR ( 100 ) ; 
DECLARE MSG CHAR ( 1000 ) ; 
SELECT ( CASE WHEN NROW . DAT > T . DAT 
THEN CHAR ( NROW . DAT , EUR ) ELSE CHAR ( T . DAT , EUR ) END ) 
CONCAT ' AND ' CONCAT ( CASE WHEN NROW . DATTO > T . DATTO 
THEN CHAR ( T . DATTO , EUR ) ELSE CHAR ( NROW . DATTO , EUR ) END ) 
INTO SQLP_L2 . CNT FROM DWH . F115RESTRUCT T 
WHERE NROW . LEVEL_TYP = T . LEVEL_TYP AND NROW . ROW_KEY = T . ROW_KEY AND NROW . CHECK_TYP = T . CHECK_TYP 
AND T . DAT <= T . DATTO 
AND ( ( NROW . DAT BETWEEN T . DAT AND T . DATTO ) 
OR ( NROW . DATTO BETWEEN T . DAT AND T . DATTO ) 
OR ( T . DAT BETWEEN NROW . DAT AND NROW . DATTO ) 
OR ( T . DATTO BETWEEN NROW . DAT AND NROW . DATTO ) 
) 
FETCH FIRST 1 ROWS ONLY ; 
IF ( SQLP_L2 . CNT IS NOT NULL ) 
THEN SET SQLP_L2 . MSG = '   -    ' 
CONCAT 'LEVEL_TYP=' CONCAT TRIM ( QSYS2 . CHAR ( NROW . LEVEL_TYP ) ) CONCAT ', ' 
CONCAT 'ROW_KEY = ' CONCAT TRIM ( QSYS2 . CHAR ( NROW . ROW_KEY ) ) CONCAT ', ' 
CONCAT 'CHECK_TYP = ' CONCAT TRIM ( QSYS2 . CHAR ( NROW . CHECK_TYP ) ) 
CONCAT '    ' CONCAT SQLP_L2 . CNT ; 
SIGNAL SQLSTATE VALUE '99999' SET MESSAGE_TEXT = SQLP_L2 . MSG ; 
END IF ; 
END  ; 
  
COMMENT ON TRIGGER DWH.F115RESTRUCT_I IS 'Check 1 row LEVEL_TYP,ROW_KEY, CHECK_TYP in 1 moment of time' ; 
  
