CREATE TRIGGER DWH.F117_CSTR_I 
	BEFORE INSERT ON DWH.F117_CSTR 
	REFERENCING NEW AS NROW 
	FOR EACH ROW 
	MODE DB2ROW 
	SET OPTION  ALWBLK = *ALLREAD , 
	ALWCPYDTA = *OPTIMIZE , 
	COMMIT = *NONE , 
	DECRESULT = (31, 31, 00) , 
	DFTRDBCOL = *NONE , 
	DYNDFTCOL = *NO , 
	DYNUSRPRF = *USER , 
	SRTSEQ = *HEX   
	BEGIN ATOMIC DECLARE CNT CHAR ( 100 ) ; 
DECLARE MSG CHAR ( 1000 ) ; 
SELECT ( CASE WHEN NROW . DAT > T . DAT 
THEN CHAR ( NROW . DAT , EUR ) ELSE CHAR ( T . DAT , EUR ) END ) 
CONCAT ' AND ' CONCAT ( CASE WHEN NROW . DATTO > T . DATTO 
THEN CHAR ( T . DATTO , EUR ) ELSE CHAR ( NROW . DATTO , EUR ) END ) 
INTO SQLP_L2 . CNT FROM DWH . F117_CSTR T 
WHERE NROW . CNUM = T . CNUM 
AND T . DAT <= T . DATTO 
AND ( ( NROW . DAT BETWEEN T . DAT AND T . DATTO ) 
OR ( NROW . DATTO BETWEEN T . DAT AND T . DATTO ) 
OR ( T . DAT BETWEEN NROW . DAT AND NROW . DATTO ) ) 
FETCH FIRST 1 ROWS ONLY ; 
IF ( SQLP_L2 . CNT IS NOT NULL ) 
THEN SET SQLP_L2 . MSG = '   -    CNUM=' 
CONCAT TRIM ( QSYS2 . CHAR ( NROW . CNUM ) ) 
CONCAT '    ' CONCAT SQLP_L2 . CNT ; 
SIGNAL SQLSTATE VALUE '99999' SET MESSAGE_TEXT = SQLP_L2 . MSG ; 
END IF ; 
END  ; 
  
COMMENT ON TRIGGER DWH.F117_CSTR_I IS 'Check 1 row CNUM in 1 moment of time' ; 
  
CREATE TRIGGER DWH.F117_CSTR_U 
	BEFORE UPDATE OF CNUM , DATTO , DAT ON DWH.F117_CSTR 
	REFERENCING OLD AS OROW 
	NEW AS NROW 
	FOR EACH ROW 
	MODE DB2ROW 
	SET OPTION  ALWBLK = *ALLREAD , 
	ALWCPYDTA = *OPTIMIZE , 
	COMMIT = *NONE , 
	DECRESULT = (31, 31, 00) , 
	DFTRDBCOL = *NONE , 
	DYNDFTCOL = *NO , 
	DYNUSRPRF = *USER , 
	SRTSEQ = *HEX   
	WHEN ( ( NROW . CNUM <> OROW . CNUM 
	OR NROW . DAT <> OROW . DAT 
	OR NROW . DATTO <> OROW . DATTO ) 
	AND NOT ( NROW . DATTO < NROW . DAT ) ) 
	BEGIN ATOMIC DECLARE CNT CHAR ( 100 ) ; 
DECLARE MSG CHAR ( 1000 ) ; 
SELECT ( CASE WHEN NROW . DAT > T . DAT 
THEN CHAR ( NROW . DAT , EUR ) ELSE CHAR ( T . DAT , EUR ) END ) 
CONCAT ' AND ' CONCAT ( CASE WHEN NROW . DATTO > T . DATTO 
THEN CHAR ( T . DATTO , EUR ) ELSE CHAR ( NROW . DATTO , EUR ) END ) 
INTO SQLP_L3 . CNT FROM DWH . F117_CSTR T 
WHERE NROW . CNUM = T . CNUM 
AND T . DAT <= T . DATTO 
AND ( ( NROW . DAT BETWEEN T . DAT AND T . DATTO ) 
OR ( NROW . DATTO BETWEEN T . DAT AND T . DATTO ) 
OR ( T . DAT BETWEEN NROW . DAT AND NROW . DATTO ) ) 
AND NOT ( OROW . CNUM = T . CNUM 
AND OROW . DAT = T . DAT 
AND OROW . DATTO = T . DATTO ) 
FETCH FIRST 1 ROWS ONLY ; 
IF ( SQLP_L3 . CNT IS NOT NULL ) 
THEN SET SQLP_L3 . MSG = '   -    CNUM=' 
CONCAT TRIM ( QSYS2 . CHAR ( NROW . CNUM ) ) 
CONCAT '    ' CONCAT SQLP_L3 . CNT ; 
SIGNAL SQLSTATE VALUE '99999' SET MESSAGE_TEXT = SQLP_L3 . MSG ; END IF ; END  ; 
  
COMMENT ON TRIGGER DWH.F117_CSTR_U IS 'Check 1 row CNUM in 1 moment of time' ; 
  
