	REFERENCING NEW AS NROW 
	FOR EACH ROW 
	MODE DB2ROW 
	SET OPTION  ALWBLK = *ALLREAD , 
	ALWCPYDTA = *OPTIMIZE , 
	COMMIT = *NONE , 
	DECRESULT = (31, 31, 00) , 
	DFTRDBCOL = *NONE , 
	DYNDFTCOL = *NO , 
	DYNUSRPRF = *USER , 
	SRTSEQ = *HEX   
	BEGIN ATOMIC DECLARE CNT CHAR ( 100 ) ; 
DECLARE MSG CHAR ( 1000 ) ; 
SELECT ( CASE WHEN NROW . DAT > DWH . F257ACC2 . DAT 
THEN CHAR ( NROW . DAT , EUR ) ELSE CHAR ( DWH . F257ACC2 . DAT , EUR ) END ) 
CONCAT ' AND ' CONCAT ( CASE WHEN NROW . DATTO > DWH . F257ACC2 . DATTO 
THEN CHAR ( DWH . F257ACC2 . DATTO , EUR ) ELSE CHAR ( NROW . DATTO , EUR ) END ) 
INTO SQLP_L2 . CNT FROM DWH . F257ACC2 
WHERE NROW . ROW_ID = DWH . F257ACC2 . ROW_ID 
AND NROW . DT_ACC2 = DWH . F257ACC2 . DT_ACC2 
AND NROW . CT_ACC2 = DWH . F257ACC2 . CT_ACC2 
AND DWH . F257ACC2 . DAT <= DWH . F257ACC2 . DATTO 
AND ( ( NROW . DAT BETWEEN DWH . F257ACC2 . DAT AND DWH . F257ACC2 . DATTO ) 
OR ( NROW . DATTO BETWEEN DWH . F257ACC2 . DAT AND DWH . F257ACC2 . DATTO ) 
OR ( DWH . F257ACC2 . DAT BETWEEN NROW . DAT AND NROW . DATTO ) ) 
FETCH FIRST 1 ROWS ONLY ; 
IF ( SQLP_L2 . CNT IS NOT NULL ) 
THEN SET SQLP_L2 . MSG = '   -    ROW_ID=' 
CONCAT TRIM ( QSYS2 . CHAR ( NROW . ROW_ID ) ) CONCAT ', DT_ACC2=' CONCAT TRIM ( NROW . DT_ACC2 ) 
CONCAT ', CT_ACC2=' CONCAT TRIM ( NROW . CT_ACC2 ) 
CONCAT '    ' CONCAT SQLP_L2 . CNT ; 
SIGNAL SQLSTATE VALUE '99999' SET MESSAGE_TEXT = SQLP_L2 . MSG ; 
-- VALUES( RAISE_ERROR( '99999', MSG ) ); 
END IF ; 
END  ; 
  
