	SNRNAM_NEREZ FOR COLUMN SNRNA00001 VARCHAR(160) CCSID 1025 NOT NULL , 
	DAT DATE NOT NULL DEFAULT CURRENT_DATE , 
	DATTO DATE DEFAULT DATE (  '2029-01-01'  ) ) ; 
  
CREATE TRIGGER DWH.F402_NEREZ91_I 
	BEFORE INSERT ON DWH.F402_NEREZ91 
	REFERENCING NEW AS NROW 
	FOR EACH ROW 
	MODE DB2ROW 
	SET OPTION  ALWBLK = *ALLREAD , 
	ALWCPYDTA = *OPTIMIZE , 
	COMMIT = *NONE , 
	DECRESULT = (31, 31, 00) , 
	DFTRDBCOL = *NONE , 
	DYNDFTCOL = *NO , 
	DYNUSRPRF = *USER , 
	SRTSEQ = *HEX   
	BEGIN ATOMIC DECLARE CNT CHAR ( 100 ) ; 
DECLARE MSG CHAR ( 1000 ) ; 
SELECT ( CASE WHEN NROW . DAT > T . DAT 
THEN CHAR ( NROW . DAT , EUR ) ELSE CHAR ( T . DAT , EUR ) END ) 
CONCAT ' AND ' CONCAT ( CASE WHEN NROW . DATTO > T . DATTO 
THEN CHAR ( T . DATTO , EUR ) ELSE CHAR ( NROW . DATTO , EUR ) END ) 
INTO SQLP_L2 . CNT FROM DWH . F402_NEREZ91 T 
WHERE TRIM ( QSYS2 . UCASE ( DWH . F402NAME2 ( NROW . SNRNAM_NEREZ ) ) ) = TRIM ( QSYS2 . UCASE ( DWH . F402NAME2 ( T . SNRNAM_NEREZ ) ) ) 
AND T . DAT <= T . DATTO 
AND ( ( NROW . DAT BETWEEN T . DAT AND T . DATTO ) 
OR ( NROW . DATTO BETWEEN T . DAT AND T . DATTO ) 
OR ( T . DAT BETWEEN NROW . DAT AND NROW . DATTO ) ) 
FETCH FIRST 1 ROWS ONLY ; 
IF ( SQLP_L2 . CNT IS NOT NULL ) 
THEN SET SQLP_L2 . MSG = '   -    SNRNAM_NEREZ=' 
CONCAT TRIM ( QSYS2 . CHAR ( NROW . SNRNAM_NEREZ ) ) 
CONCAT '    ' CONCAT SQLP_L2 . CNT ; 
SIGNAL SQLSTATE VALUE '99999' SET MESSAGE_TEXT = SQLP_L2 . MSG ; 
END IF ; 
END  ; 
  
COMMENT ON TRIGGER DWH.F402_NEREZ91_I IS 'Check 1 row SNRNAM_NEREZ in 1 moment of time' ; 
  
CREATE TRIGGER DWH.F402_NEREZ91_U 
	BEFORE UPDATE OF SNRNAM_NEREZ , DATTO , DAT ON DWH.F402_NEREZ91 
