THEN CHAR ( T . DATTO , EUR ) ELSE CHAR ( NROW . DATTO , EUR ) END ) 
INTO SQLP_L3 . CNT FROM DWH . F402_NEREZ91 T 
WHERE TRIM ( QSYS2 . UCASE ( DWH . F402NAME2 ( NROW . SNRNAM_NEREZ ) ) ) = TRIM ( QSYS2 . UCASE ( DWH . F402NAME2 ( T . SNRNAM_NEREZ ) ) ) 
AND T . DAT <= T . DATTO 
AND ( ( NROW . DAT BETWEEN T . DAT AND T . DATTO ) 
OR ( NROW . DATTO BETWEEN T . DAT AND T . DATTO ) 
OR ( T . DAT BETWEEN NROW . DAT AND NROW . DATTO ) ) 
AND NOT ( TRIM ( QSYS2 . UCASE ( DWH . F402NAME2 ( OROW . SNRNAM_NEREZ ) ) ) = TRIM ( QSYS2 . UCASE ( DWH . F402NAME2 ( T . SNRNAM_NEREZ ) ) ) 
AND OROW . DAT = T . DAT 
AND OROW . DATTO = T . DATTO ) 
FETCH FIRST 1 ROWS ONLY ; 
IF ( SQLP_L3 . CNT IS NOT NULL ) 
THEN SET SQLP_L3 . MSG = '   -    SNRNAM_NEREZ=' 
CONCAT TRIM ( QSYS2 . CHAR ( NROW . SNRNAM_NEREZ ) ) 
