	DLST CHAR(2) CCSID 1025 DEFAULT NULL , 
	BPTYPE CHAR(4) CCSID 1025 DEFAULT NULL , 
	DEAL_DESCR VARCHAR(50) CCSID 1025 DEFAULT NULL , 
	DAT DATE NOT NULL DEFAULT CURRENT_DATE , 
	DATTO DATE NOT NULL DEFAULT DATE (  '2029-01-01'  ) ) ; 
  
CREATE TRIGGER DWH.F634DTYP_I1 
	BEFORE INSERT ON DWH.F634DTYP 
	REFERENCING NEW AS NROW 
	FOR EACH ROW 
	MODE DB2ROW 
	SET OPTION  ALWBLK = *ALLREAD , 
	ALWCPYDTA = *OPTIMIZE , 
	COMMIT = *NONE , 
	DECRESULT = (31, 31, 00) , 
	DFTRDBCOL = DWH , 
	DYNDFTCOL = *NO , 
	DYNUSRPRF = *USER , 
	SRTSEQ = *HEX   
	BEGIN ATOMIC DECLARE CNT CHAR ( 100 ) ; 
DECLARE MSG CHAR ( 1000 ) ; 
SELECT ( CASE WHEN NROW . DAT > DWH . F634DTYP . DAT 
THEN CHAR ( NROW . DAT , EUR ) ELSE CHAR ( DWH . F634DTYP . DAT , EUR ) END ) 
CONCAT ' AND ' CONCAT ( CASE WHEN NROW . DATTO > DWH . F634DTYP . DATTO 
THEN CHAR ( DWH . F634DTYP . DATTO , EUR ) ELSE CHAR ( NROW . DATTO , EUR ) END ) 
INTO SQLP_L2 . CNT FROM DWH . F634DTYP 
WHERE NROW . DTYP = DWH . F634DTYP . DTYP 
AND NROW . DLST = DWH . F634DTYP . DLST 
AND DWH . F634DTYP . DAT <= DWH . F634DTYP . DATTO 
AND ( ( NROW . DAT BETWEEN DWH . F634DTYP . DAT AND DWH . F634DTYP . DATTO ) 
OR ( NROW . DATTO BETWEEN DWH . F634DTYP . DAT AND DWH . F634DTYP . DATTO ) 
OR ( DWH . F634DTYP . DAT BETWEEN NROW . DAT AND NROW . DATTO ) ) 
FETCH FIRST 1 ROWS ONLY ; 
IF ( SQLP_L2 . CNT IS NOT NULL ) 
THEN SET SQLP_L2 . MSG = '   -    DTYP=' 
CONCAT TRIM ( NROW . DTYP ) CONCAT ', DLST=' CONCAT TRIM ( NROW . DLST ) 
CONCAT '    ' CONCAT SQLP_L2 . CNT ; 
SIGNAL SQLSTATE VALUE '99999' SET MESSAGE_TEXT = SQLP_L2 . MSG ; 
-- VALUES( RAISE_ERROR( '99999', MSG ) ); 
END IF ; 
END  ; 
  
COMMENT ON TRIGGER DWH.F634DTYP_I1 IS 'Check 1 row DTYP,DLST in 1 moment of time' ; 
  
CREATE TRIGGER DWH.F634DTYP_U1 
	BEFORE UPDATE OF DTYP , DLST , DATTO , DAT ON DWH.F634DTYP 
	REFERENCING OLD AS OROW 
	NEW AS NROW 
	FOR EACH ROW 
	MODE DB2ROW 
	SET OPTION  ALWBLK = *ALLREAD , 
	ALWCPYDTA = *OPTIMIZE , 
	COMMIT = *NONE , 
	DECRESULT = (31, 31, 00) , 
	DFTRDBCOL = DWH , 
	DYNDFTCOL = *NO , 
	DYNUSRPRF = *USER , 
	SRTSEQ = *HEX   
	WHEN ( ( NROW . DTYP <> OROW . DTYP 
	OR NROW . DLST <> OROW . DLST 
	OR NROW . DAT <> OROW . DAT 
	OR NROW . DATTO <> OROW . DATTO ) 
	AND NOT ( NROW . DATTO < NROW . DAT ) ) 
	BEGIN ATOMIC DECLARE CNT CHAR ( 100 ) ; 
DECLARE MSG CHAR ( 1000 ) ; 
SELECT ( CASE WHEN NROW . DAT > DEALS . DAT 
THEN CHAR ( NROW . DAT , EUR ) ELSE CHAR ( DEALS . DAT , EUR ) END ) 
CONCAT ' AND ' CONCAT ( CASE WHEN NROW . DATTO > DEALS . DATTO 
THEN CHAR ( DEALS . DATTO , EUR ) ELSE CHAR ( NROW . DATTO , EUR ) END ) 
INTO SQLP_L3 . CNT FROM DWH . F634DTYP DEALS 
WHERE NROW . DTYP = DEALS . DTYP 
AND NROW . DLST = DEALS . DLST 
AND DEALS . DAT <= DEALS . DATTO 
AND ( ( NROW . DAT BETWEEN DEALS . DAT AND DEALS . DATTO ) 
OR ( NROW . DATTO BETWEEN DEALS . DAT AND DEALS . DATTO ) 
OR ( DEALS . DAT BETWEEN NROW . DAT AND NROW . DATTO ) ) 
AND NOT ( OROW . DTYP = DEALS . DTYP 
AND OROW . DLST = DEALS . DLST 
AND OROW . DAT = DEALS . DAT 
AND OROW . DATTO = DEALS . DATTO ) 
FETCH FIRST 1 ROWS ONLY ; 
IF ( SQLP_L3 . CNT IS NOT NULL ) 
THEN SET SQLP_L3 . MSG = '   -    DTYP=' 
CONCAT TRIM ( NROW . DTYP ) CONCAT ', DLST=' CONCAT TRIM ( NROW . DLST ) 
CONCAT '    ' CONCAT SQLP_L3 . CNT ; 
SIGNAL SQLSTATE VALUE '99999' SET MESSAGE_TEXT = SQLP_L3 . MSG ; END IF ; 
END  ; 
  
COMMENT ON TRIGGER DWH.F634DTYP_U1 IS 'Check 1 row DTYP,DLST in 1 moment of time' ; 
  
CREATE TRIGGER DWH.F634DTYP_U2 
	AFTER UPDATE OF DATTO , DAT ON DWH.F634DTYP 
