	INVISIBLE INTEGER DEFAULT NULL , 
	DAT DATE NOT NULL DEFAULT '2009-01-01' , 
	DATTO DATE NOT NULL DEFAULT '2029-01-01' ) ; 
  
CREATE TRIGGER DWH.F701_DEALS_I1 
	BEFORE INSERT ON DWH.F701_DEALS 
	REFERENCING NEW AS NROW 
	FOR EACH ROW 
	MODE DB2ROW 
	SET OPTION  ALWBLK = *ALLREAD , 
	ALWCPYDTA = *OPTIMIZE , 
	COMMIT = *NONE , 
	CLOSQLCSR = *ENDMOD , 
	DECRESULT = (31, 31, 00) , 
	DFTRDBCOL = DWH , 
	DYNDFTCOL = *NO , 
	DYNUSRPRF = *USER , 
	SRTSEQ = *HEX   
	BEGIN ATOMIC 
DECLARE CNT CHAR ( 100 ) ; 
DECLARE MSG CHAR ( 1000 ) ; 
SELECT ( CASE WHEN NROW . DAT > OROW . DAT 
THEN CHAR ( NROW . DAT , EUR ) 
ELSE CHAR ( OROW . DAT , EUR ) 
END ) 
CONCAT ' AND ' 
CONCAT ( CASE WHEN NROW . DATTO > OROW . DATTO 
THEN CHAR ( OROW . DATTO , EUR ) 
ELSE CHAR ( NROW . DATTO , EUR ) 
END ) 
INTO SQLP_L2 . CNT 
FROM DWH . F701_DEALS OROW 
WHERE NROW . DTYP = OROW . DTYP AND 
NROW . DLST = OROW . DLST AND 
	NROW . CLS = OROW . CLS AND 
	OROW . DAT <= OROW . DATTO AND 
		( ( NROW . DAT BETWEEN OROW . DAT AND OROW . DATTO ) OR 
( NROW . DATTO BETWEEN OROW . DAT AND OROW . DATTO ) OR 
	( OROW . DAT BETWEEN NROW . DAT AND NROW . DATTO ) ) 
FETCH FIRST 1 ROWS ONLY ; 
  
IF ( SQLP_L2 . CNT IS NOT NULL ) 
THEN SET SQLP_L2 . MSG = '   -    DTYP=' 
CONCAT TRIM ( NROW . DTYP ) 
							CONCAT ', DLST=' CONCAT TRIM ( NROW . DLST ) 
							CONCAT ', CLS=' CONCAT TRIM ( NROW . CLS ) 
CONCAT '    ' CONCAT SQLP_L2 . CNT ; 
SIGNAL SQLSTATE VALUE '99999' SET MESSAGE_TEXT = SQLP_L2 . MSG ; 
-- VALUES( RAISE_ERROR( '99999', MSG ) ); 
END IF ; 
END  ; 
  
COMMENT ON TRIGGER DWH.F701_DEALS_I1 IS 'Check 1 row DTYP,DLST, CLS in 1 moment of time' ; 
  
CREATE TRIGGER DWH.F701_DEALS_U1 
	BEFORE UPDATE OF DTYP , DLST , DATTO , DAT ON DWH.F701_DEALS 
	REFERENCING OLD AS OROW 
	NEW AS NROW 
	FOR EACH ROW 
	MODE DB2ROW 
	SET OPTION  ALWBLK = *ALLREAD , 
	ALWCPYDTA = *OPTIMIZE , 
