CREATE TRIGGER DWH.F701_RATES_I1 
	BEFORE INSERT ON DWH.F701_RATES 
	REFERENCING NEW AS NROW 
	FOR EACH ROW 
	MODE DB2ROW 
	SET OPTION  ALWBLK = *ALLREAD , 
	ALWCPYDTA = *OPTIMIZE , 
	COMMIT = *NONE , 
	DECRESULT = (31, 31, 00) , 
	DFTRDBCOL = DWH , 
	DYNDFTCOL = *NO , 
	DYNUSRPRF = *USER , 
	SRTSEQ = *HEX   
	BEGIN ATOMIC DECLARE CNT CHAR ( 100 ) ; 
DECLARE MSG CHAR ( 1000 ) ; 
SELECT ( CASE WHEN NROW . DAT > DWH . F701_RATES . DAT 
THEN CHAR ( NROW . DAT , EUR ) ELSE CHAR ( DWH . F701_RATES . DAT , EUR ) END ) 
CONCAT ' AND ' CONCAT ( CASE WHEN NROW . DATTO > DWH . F701_RATES . DATTO 
THEN CHAR ( DWH . F701_RATES . DATTO , EUR ) ELSE CHAR ( NROW . DATTO , EUR ) END ) 
INTO SQLP_L2 . CNT FROM DWH . F701_RATES 
WHERE NROW . RTYP = DWH . F701_RATES . RTYP 
AND NROW . TERM_ID = DWH . F701_RATES . TERM_ID 
AND NROW . CBCCY = DWH . F701_RATES . CBCCY 
AND DWH . F701_RATES . DAT <= DWH . F701_RATES . DATTO 
AND ( ( NROW . DAT BETWEEN DWH . F701_RATES . DAT AND DWH . F701_RATES . DATTO ) 
OR ( NROW . DATTO BETWEEN DWH . F701_RATES . DAT AND DWH . F701_RATES . DATTO ) 
OR ( DWH . F701_RATES . DAT BETWEEN NROW . DAT AND NROW . DATTO ) ) 
FETCH FIRST 1 ROWS ONLY ; 
IF ( SQLP_L2 . CNT IS NOT NULL ) 
THEN SET SQLP_L2 . MSG = '   -    RTYP=' 
CONCAT TRIM ( NROW . RTYP ) CONCAT ', TERM_ID=' CONCAT TRIM ( NROW . TERM_ID ) 
CONCAT ', CBCCY=' CONCAT TRIM ( NROW . CBCCY ) 
CONCAT '    ' CONCAT SQLP_L2 . CNT ; 
SIGNAL SQLSTATE VALUE '99999' SET MESSAGE_TEXT = SQLP_L2 . MSG ; 
-- VALUES( RAISE_ERROR( '99999', MSG ) ); 
END IF ; 
END  ; 
  
COMMENT ON TRIGGER DWH.F701_RATES_I1 IS 'Check 1 row rtyp,term_id,cbccy in 1 moment of time' ; 
  
CREATE TRIGGER DWH.F701_RATES_U1 
	BEFORE UPDATE OF RTYP , TERM_ID , CBCCY , DATTO , DAT ON DWH.F701_RATES 
	REFERENCING OLD AS OROW 
	NEW AS NROW 
	FOR EACH ROW 
	MODE DB2ROW 
	SET OPTION  ALWBLK = *ALLREAD , 
	ALWCPYDTA = *OPTIMIZE , 
	COMMIT = *NONE , 
	DECRESULT = (31, 31, 00) , 
	DFTRDBCOL = DWH , 
	DYNDFTCOL = *NO , 
	DYNUSRPRF = *USER , 
	SRTSEQ = *HEX   
	WHEN ( ( NROW . RTYP <> OROW . RTYP 
	OR NROW . TERM_ID <> OROW . TERM_ID 
	OR NROW . CBCCY <> OROW . CBCCY 
	OR NROW . DAT <> OROW . DAT 
	OR NROW . DATTO <> OROW . DATTO ) 
	AND NOT ( NROW . DATTO < NROW . DAT ) ) 
	BEGIN ATOMIC DECLARE CNT CHAR ( 100 ) ; 
DECLARE MSG CHAR ( 1000 ) ; 
SELECT ( CASE WHEN NROW . DAT > RATES . DAT 
THEN CHAR ( NROW . DAT , EUR ) ELSE CHAR ( RATES . DAT , EUR ) END ) 
CONCAT ' AND ' CONCAT ( CASE WHEN NROW . DATTO > RATES . DATTO 
THEN CHAR ( RATES . DATTO , EUR ) ELSE CHAR ( NROW . DATTO , EUR ) END ) 
INTO SQLP_L3 . CNT FROM DWH . F701_RATES RATES 
WHERE NROW . RTYP = RATES . RTYP 
AND NROW . TERM_ID = RATES . TERM_ID 
AND NROW . CBCCY = RATES . CBCCY 
AND RATES . DAT <= RATES . DATTO 
AND ( ( NROW . DAT BETWEEN RATES . DAT AND RATES . DATTO ) 
OR ( NROW . DATTO BETWEEN RATES . DAT AND RATES . DATTO ) 
OR ( RATES . DAT BETWEEN NROW . DAT AND NROW . DATTO ) ) 
AND NOT ( OROW . RTYP = RATES . RTYP 
AND OROW . TERM_ID = RATES . TERM_ID 
AND OROW . CBCCY = RATES . CBCCY 
AND OROW . DAT = RATES . DAT 
AND OROW . DATTO = RATES . DATTO ) 
FETCH FIRST 1 ROWS ONLY ; 
IF ( SQLP_L3 . CNT IS NOT NULL ) 
THEN SET SQLP_L3 . MSG = '   -    RTYP=' 
CONCAT TRIM ( NROW . RTYP ) CONCAT ', TERM_ID=' CONCAT TRIM ( NROW . TERM_ID ) 
CONCAT ', CBCCY=' CONCAT TRIM ( NROW . CBCCY ) 
CONCAT '    ' CONCAT SQLP_L3 . CNT ; 
SIGNAL SQLSTATE VALUE '99999' SET MESSAGE_TEXT = SQLP_L3 . MSG ; END IF ; END  ; 
  
COMMENT ON TRIGGER DWH.F701_RATES_U1 IS 'Check 1 row rtyp,term_id,cbccy in 1 moment of time' ; 
  
CREATE TRIGGER DWH.F701_RATES_U2 
	AFTER UPDATE OF DATTO , DAT ON DWH.F701_RATES 
	REFERENCING OLD AS OROW 
	NEW AS NROW 
	FOR EACH ROW 
	MODE DB2ROW 
	SET OPTION  ALWBLK = *ALLREAD , 
	ALWCPYDTA = *OPTIMIZE , 
	COMMIT = *NONE , 
	DECRESULT = (31, 31, 00) , 
	DFTRDBCOL = DWH , 
	DYNDFTCOL = *NO , 
	DYNUSRPRF = *USER , 
	SRTSEQ = *HEX   
	WHEN ( ( NROW . DAT <> OROW . DAT 
	OR NROW . DATTO <> OROW . DATTO ) 
	AND NROW . DATTO < NROW . DAT ) 
	BEGIN ATOMIC 
DELETE FROM DWH . F701_RATES R 
WHERE R . RTYP = NROW . RTYP 
AND R . TERM_ID = NROW . TERM_ID 
AND R . CBCCY = NROW . CBCCY 
AND R . DAT = NROW . DAT 
AND R . DATTO = NROW . DATTO; 
END  ; 
  
