	CONSTRAINT DWH.Q_DWH_FCDDLBAL_INVOICE_ID_00001 PRIMARY KEY( INVOICE_ID , RLNTYPE , DAT ) ) ; 
  
CREATE TRIGGER DWH.FCDDLBAL_I 
	BEFORE INSERT ON DWH.FCDDLBAL 
	REFERENCING NEW AS NROW 
	FOR EACH ROW 
	MODE DB2ROW 
	SET OPTION  ALWBLK = *ALLREAD , 
	ALWCPYDTA = *OPTIMIZE , 
	COMMIT = *NONE , 
	DECRESULT = (31, 31, 00) , 
	DFTRDBCOL = *NONE , 
	DYNDFTCOL = *NO , 
	DYNUSRPRF = *USER , 
	SRTSEQ = *HEX   
	BEGIN ATOMIC DECLARE CNT CHAR ( 100 ) ; 
DECLARE MSG CHAR ( 1000 ) ; 
SELECT ( CASE WHEN NROW . DAT > T . DAT 
THEN CHAR ( NROW . DAT , EUR ) ELSE CHAR ( T . DAT , EUR ) END ) 
CONCAT ' AND ' CONCAT ( CASE WHEN NROW . DATTO > T . DATTO 
THEN CHAR ( T . DATTO , EUR ) ELSE CHAR ( NROW . DATTO , EUR ) END ) 
INTO SQLP_L2 . CNT FROM DWH . FCDDLBAL T 
WHERE NROW . INVOICE_ID = T . INVOICE_ID 
AND NROW . RLNTYPE = T . RLNTYPE 
AND T . DAT <= T . DATTO 
AND ( ( NROW . DAT BETWEEN T . DAT AND T . DATTO ) 
OR ( NROW . DATTO BETWEEN T . DAT AND T . DATTO ) 
OR ( T . DAT BETWEEN NROW . DAT AND NROW . DATTO ) ) 
FETCH FIRST 1 ROWS ONLY ; 
IF ( SQLP_L2 . CNT IS NOT NULL ) 
THEN SET SQLP_L2 . MSG = '   -    INVOICE_ID=' 
CONCAT TRIM ( NROW . INVOICE_ID ) CONCAT ', RLNTYPE=' CONCAT TRIM ( QSYS2 . CHAR ( NROW . RLNTYPE ) ) 
CONCAT '    ' CONCAT SQLP_L2 . CNT ; 
SIGNAL SQLSTATE VALUE '99999' SET MESSAGE_TEXT = SQLP_L2 . MSG ; 
END IF ; 
END  ; 
  
COMMENT ON TRIGGER DWH.FCDDLBAL_I IS 'Check 1 row INVOICE_ID,RLNTYPE in 1 moment of time' ; 
  
CREATE TRIGGER DWH.FCDDLBAL_U 
	BEFORE UPDATE OF INVOICE_ID , RLNTYPE , DATTO , DAT ON DWH.FCDDLBAL 
	REFERENCING OLD AS OROW 
	NEW AS NROW 
	FOR EACH ROW 
	MODE DB2ROW 
	SET OPTION  ALWBLK = *ALLREAD , 
	ALWCPYDTA = *OPTIMIZE , 
	COMMIT = *NONE , 
	DECRESULT = (31, 31, 00) , 
	DFTRDBCOL = *NONE , 
	DYNDFTCOL = *NO , 
	DYNUSRPRF = *USER , 
	SRTSEQ = *HEX   
	WHEN ( ( NROW . INVOICE_ID <> OROW . INVOICE_ID 
	OR NROW . RLNTYPE <> OROW . RLNTYPE 
	OR NROW . DAT <> OROW . DAT 
	OR NROW . DATTO <> OROW . DATTO ) 
	AND NOT ( NROW . DATTO < NROW . DAT ) ) 
	BEGIN ATOMIC DECLARE CNT CHAR ( 100 ) ; 
DECLARE MSG CHAR ( 1000 ) ; 
SELECT ( CASE WHEN NROW . DAT > T . DAT 
THEN CHAR ( NROW . DAT , EUR ) ELSE CHAR ( T . DAT , EUR ) END ) 
CONCAT ' AND ' CONCAT ( CASE WHEN NROW . DATTO > T . DATTO 
THEN CHAR ( T . DATTO , EUR ) ELSE CHAR ( NROW . DATTO , EUR ) END ) 
INTO SQLP_L3 . CNT FROM DWH . FCDDLBAL T 
WHERE NROW . INVOICE_ID = T . INVOICE_ID 
AND NROW . RLNTYPE = T . RLNTYPE 
AND T . DAT <= T . DATTO 
AND ( ( NROW . DAT BETWEEN T . DAT AND T . DATTO ) 
OR ( NROW . DATTO BETWEEN T . DAT AND T . DATTO ) 
OR ( T . DAT BETWEEN NROW . DAT AND NROW . DATTO ) ) 
AND NOT ( OROW . INVOICE_ID = T . INVOICE_ID 
AND OROW . RLNTYPE = T . RLNTYPE 
AND OROW . DAT = T . DAT 
AND OROW . DATTO = T . DATTO ) 
FETCH FIRST 1 ROWS ONLY ; 
IF ( SQLP_L3 . CNT IS NOT NULL ) 
THEN SET SQLP_L3 . MSG = '   -    INVOICE_ID=' 
CONCAT TRIM ( NROW . INVOICE_ID ) CONCAT ', RLNTYPE=' CONCAT TRIM ( QSYS2 . CHAR ( NROW . RLNTYPE ) ) 
CONCAT '    ' CONCAT SQLP_L3 . CNT ; 
SIGNAL SQLSTATE VALUE '99999' SET MESSAGE_TEXT = SQLP_L3 . MSG ; END IF ; END  ; 
  
COMMENT ON TRIGGER DWH.FCDDLBAL_U IS 'Check 1 row INVOICE_ID,RLNTYPE in 1 moment of time' ; 
  
