	AGR_ID INTEGER DEFAULT NULL , 
	CNUM CHAR(8) CCSID 1025 DEFAULT NULL , 
	FACT CHAR(3) CCSID 1025 DEFAULT NULL , 
	FCNO CHAR(2) CCSID 1025 DEFAULT NULL , 
	DAT DATE DEFAULT NULL , 
	DATTO DATE DEFAULT NULL , 
