CREATE TRIGGER DWH.FCLAGRLST_I 
	BEFORE INSERT ON DWH.FCLAGRLST 
	REFERENCING NEW AS NROW 
	FOR EACH ROW 
	MODE DB2ROW 
	SET OPTION  ALWBLK = *ALLREAD , 
	ALWCPYDTA = *OPTIMIZE , 
	COMMIT = *NONE , 
	DECRESULT = (31, 31, 00) , 
	DFTRDBCOL = *NONE , 
	DYNDFTCOL = *NO , 
	DYNUSRPRF = *USER , 
	SRTSEQ = *HEX   
	BEGIN ATOMIC DECLARE CNT CHAR ( 100 ) ; 
DECLARE MSG CHAR ( 1000 ) ; 
SELECT ( CASE WHEN NROW . DAT > T . DAT 
THEN CHAR ( NROW . DAT , EUR ) ELSE CHAR ( T . DAT , EUR ) END ) 
CONCAT ' AND ' CONCAT ( CASE WHEN NROW . DATTO > T . DATTO 
THEN CHAR ( T . DATTO , EUR ) ELSE CHAR ( NROW . DATTO , EUR ) END ) 
INTO SQLP_L2 . CNT FROM DWH . FCLAGRLST T 
-- 
WHERE TRIM ( QSYS2 . CHAR ( NROW . AGR_ID ) ) || '_' || TRIM ( NROW . CNUM ) || '_' || TRIM ( NROW . FACT ) || '/' || TRIM ( NROW . FCNO ) 
= TRIM ( QSYS2 . CHAR ( T . AGR_ID ) ) || '_' || TRIM ( T . CNUM ) || '_' || TRIM ( T . FACT ) || '/' || TRIM ( T . FCNO ) 
AND T . DAT <= T . DATTO 
AND ( ( NROW . DAT BETWEEN T . DAT AND T . DATTO ) 
OR ( NROW . DATTO BETWEEN T . DAT AND T . DATTO ) 
OR ( T . DAT BETWEEN NROW . DAT AND NROW . DATTO ) ) 
FETCH FIRST 1 ROWS ONLY ; 
IF ( SQLP_L2 . CNT IS NOT NULL ) 
THEN SET SQLP_L2 . MSG = '   -    = {' 
CONCAT TRIM ( QSYS2 . CHAR ( NROW . AGR_ID ) ) || '_' || TRIM ( NROW . CNUM ) || '_' || TRIM ( NROW . FACT ) || '/' || TRIM ( NROW . FCNO ) 
CONCAT '}    ' CONCAT SQLP_L2 . CNT ; 
SIGNAL SQLSTATE VALUE '99999' SET MESSAGE_TEXT = SQLP_L2 . MSG ; 
END IF ; 
END  ; 
  
COMMENT ON TRIGGER DWH.FCLAGRLST_I IS 'Check 1 row for KEY in 1 moment of time' ; 
  
CREATE TRIGGER DWH.FCLAGRLST_U 
	BEFORE UPDATE OF CNUM , DATTO , DAT ON DWH.FCLAGRLST 
	REFERENCING OLD AS OROW 
	NEW AS NROW 
	FOR EACH ROW 
	MODE DB2ROW 
	SET OPTION  ALWBLK = *ALLREAD , 
	ALWCPYDTA = *OPTIMIZE , 
	COMMIT = *NONE , 
	DECRESULT = (31, 31, 00) , 
	DFTRDBCOL = *NONE , 
	DYNDFTCOL = *NO , 
	DYNUSRPRF = *USER , 
	SRTSEQ = *HEX   
	WHEN ( ( TRIM ( QSYS2 . CHAR ( NROW . AGR_ID ) ) || '_' || TRIM ( NROW . CNUM ) || '_' || TRIM ( NROW . FACT ) || '/' || TRIM ( NROW . FCNO ) 
	<> TRIM ( QSYS2 . CHAR ( OROW . AGR_ID ) ) || '_' || TRIM ( OROW . CNUM ) || '_' || TRIM ( OROW . FACT ) || '/' || TRIM ( OROW . FCNO ) 
	OR NROW . DAT <> OROW . DAT 
	OR NROW . DATTO <> OROW . DATTO ) 
	AND NOT ( NROW . DATTO < NROW . DAT ) ) 
	BEGIN ATOMIC DECLARE CNT CHAR ( 100 ) ; 
DECLARE MSG CHAR ( 1000 ) ; 
SELECT ( CASE WHEN NROW . DAT > T . DAT 
THEN CHAR ( NROW . DAT , EUR ) ELSE CHAR ( T . DAT , EUR ) END ) 
CONCAT ' AND ' CONCAT ( CASE WHEN NROW . DATTO > T . DATTO 
THEN CHAR ( T . DATTO , EUR ) ELSE CHAR ( NROW . DATTO , EUR ) END ) 
INTO SQLP_L3 . CNT FROM DWH . FCLAGRLST T 
WHERE TRIM ( QSYS2 . CHAR ( NROW . AGR_ID ) ) || '_' || TRIM ( NROW . CNUM ) || '_' || TRIM ( NROW . FACT ) || '/' || TRIM ( NROW . FCNO ) 
= TRIM ( QSYS2 . CHAR ( T . AGR_ID ) ) || '_' || TRIM ( T . CNUM ) || '_' || TRIM ( T . FACT ) || '/' || TRIM ( T . FCNO ) 
AND T . DAT <= T . DATTO 
AND ( ( NROW . DAT BETWEEN T . DAT AND T . DATTO ) 
OR ( NROW . DATTO BETWEEN T . DAT AND T . DATTO ) 
OR ( T . DAT BETWEEN NROW . DAT AND NROW . DATTO ) ) 
AND NOT ( TRIM ( QSYS2 . CHAR ( OROW . AGR_ID ) ) || '_' || TRIM ( OROW . CNUM ) || '_' || TRIM ( OROW . FACT ) || '/' || TRIM ( OROW . FCNO ) 
= TRIM ( QSYS2 . CHAR ( T . AGR_ID ) ) || '_' || TRIM ( T . CNUM ) || '_' || TRIM ( T . FACT ) || '/' || TRIM ( T . FCNO ) 
AND OROW . DAT = T . DAT 
AND OROW . DATTO = T . DATTO ) 
FETCH FIRST 1 ROWS ONLY ; 
IF ( SQLP_L3 . CNT IS NOT NULL ) 
THEN SET SQLP_L3 . MSG = '   -    = {' 
CONCAT TRIM ( QSYS2 . CHAR ( NROW . AGR_ID ) ) || '_' || TRIM ( NROW . CNUM ) || '_' || TRIM ( NROW . FACT ) || '/' || TRIM ( NROW . FCNO ) 
CONCAT '}    ' CONCAT SQLP_L3 . CNT ; 
SIGNAL SQLSTATE VALUE '99999' SET MESSAGE_TEXT = SQLP_L3 . MSG ; END IF ; END  ; 
  
