		BEGIN ATOMIC SET ( NROW . CHREF ) = ( SELECT QSYS2 . VALUE ( QSYS2 . MAX ( DWH . GCPAPCH1 . CHREF ) , 0 ) + 1 
	AS NEW_CHREF FROM DWH . GCPAPCH1 ) ; END  ; 
  
