CREATE TABLE DWH.RTAG_PRIOR ( 
	RTAGS_CODE VARCHAR(100) CCSID 1025 DEFAULT NULL , 
	PRIOR DECIMAL(19, 3) DEFAULT NULL , 
	DAT DATE DEFAULT NULL , 
	DATTO DATE DEFAULT NULL ) ; 
  
CREATE TRIGGER DWH.RTAG_PRIOR_I 
	BEFORE INSERT ON DWH.RTAG_PRIOR 
	REFERENCING NEW AS NROW 
	FOR EACH ROW 
	MODE DB2ROW 
	SET OPTION  ALWBLK = *ALLREAD , 
	ALWCPYDTA = *OPTIMIZE , 
	COMMIT = *NONE , 
	DECRESULT = (31, 31, 00) , 
	DFTRDBCOL = *NONE , 
	DYNDFTCOL = *NO , 
	DYNUSRPRF = *USER , 
	SRTSEQ = *HEX   
	BEGIN ATOMIC DECLARE CNT CHAR ( 100 ) ; 
DECLARE MSG CHAR ( 1000 ) ; 
SELECT ( CASE WHEN NROW . DAT > T . DAT 
THEN CHAR ( NROW . DAT , EUR ) ELSE CHAR ( T . DAT , EUR ) END ) 
CONCAT ' AND ' CONCAT ( CASE WHEN NROW . DATTO > T . DATTO 
THEN CHAR ( T . DATTO , EUR ) ELSE CHAR ( NROW . DATTO , EUR ) END ) 
INTO SQLP_L2 . CNT FROM DWH . RTAG_PRIOR T 
WHERE NROW . RTAGS_CODE = T . RTAGS_CODE 
AND T . DAT <= T . DATTO 
AND ( ( NROW . DAT BETWEEN T . DAT AND T . DATTO ) 
OR ( NROW . DATTO BETWEEN T . DAT AND T . DATTO ) 
OR ( T . DAT BETWEEN NROW . DAT AND NROW . DATTO ) ) 
FETCH FIRST 1 ROWS ONLY ; 
IF ( SQLP_L2 . CNT IS NOT NULL ) 
THEN SET SQLP_L2 . MSG = '   -    RTAGS_CODE=' 
CONCAT TRIM ( QSYS2 . CHAR ( NROW . RTAGS_CODE ) ) 
CONCAT '    ' CONCAT SQLP_L2 . CNT ; 
SIGNAL SQLSTATE VALUE '99999' SET MESSAGE_TEXT = SQLP_L2 . MSG ; 
END IF ; 
END  ; 
  
COMMENT ON TRIGGER DWH.RTAG_PRIOR_I IS 'Check 1 row RTAGS_CODE in 1 moment of time' ; 
  
CREATE TRIGGER DWH.RTAG_PRIOR_U 
	BEFORE UPDATE OF RTAGS_CODE , DATTO , DAT ON DWH.RTAG_PRIOR 
	REFERENCING OLD AS OROW 
	NEW AS NROW 
	FOR EACH ROW 
	MODE DB2ROW 
	SET OPTION  ALWBLK = *ALLREAD , 
	ALWCPYDTA = *OPTIMIZE , 
	COMMIT = *NONE , 
	DECRESULT = (31, 31, 00) , 
	DFTRDBCOL = *NONE , 
	DYNDFTCOL = *NO , 
	DYNUSRPRF = *USER , 
	SRTSEQ = *HEX   
	WHEN ( ( NROW . RTAGS_CODE <> OROW . RTAGS_CODE 
	OR NROW . DAT <> OROW . DAT 
	OR NROW . DATTO <> OROW . DATTO ) 
	AND NOT ( NROW . DATTO < NROW . DAT ) ) 
	BEGIN ATOMIC DECLARE CNT CHAR ( 100 ) ; 
DECLARE MSG CHAR ( 1000 ) ; 
SELECT ( CASE WHEN NROW . DAT > T . DAT 
THEN CHAR ( NROW . DAT , EUR ) ELSE CHAR ( T . DAT , EUR ) END ) 
CONCAT ' AND ' CONCAT ( CASE WHEN NROW . DATTO > T . DATTO 
THEN CHAR ( T . DATTO , EUR ) ELSE CHAR ( NROW . DATTO , EUR ) END ) 
INTO SQLP_L3 . CNT FROM DWH . RTAG_PRIOR T 
WHERE NROW . RTAGS_CODE = T . RTAGS_CODE 
AND T . DAT <= T . DATTO 
AND ( ( NROW . DAT BETWEEN T . DAT AND T . DATTO ) 
OR ( NROW . DATTO BETWEEN T . DAT AND T . DATTO ) 
OR ( T . DAT BETWEEN NROW . DAT AND NROW . DATTO ) ) 
AND NOT ( OROW . RTAGS_CODE = T . RTAGS_CODE 
AND OROW . DAT = T . DAT 
AND OROW . DATTO = T . DATTO ) 
FETCH FIRST 1 ROWS ONLY ; 
IF ( SQLP_L3 . CNT IS NOT NULL ) 
THEN SET SQLP_L3 . MSG = '   -    RTAGS_CODE=' 
CONCAT TRIM ( QSYS2 . CHAR ( NROW . RTAGS_CODE ) ) 
CONCAT '    ' CONCAT SQLP_L3 . CNT ; 
SIGNAL SQLSTATE VALUE '99999' SET MESSAGE_TEXT = SQLP_L3 . MSG ; END IF ; 
